import React, { useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { menuItems } from '../../utils/menuItems';
import logoImage from '../../images/white_logo.png'; // Import your logo image

export const Navigation = () => {
  const [active, setActive] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav>
      <Link to="/" className="title">
        <div className="logo">
          <img src={logoImage} alt="Logo" id="logo-nav" /> {/* Use the imported logo image */}
        </div>
      </Link>
      <div className="menu" onClick={() => setMenuOpen(!menuOpen)}>
        <span></span>
        <span></span>
        <span></span>
      </div>
      <ul className={`menu-list ${menuOpen ? 'open' : ''}`}>
        {menuItems.map((item) => (
          <li key={item.id} className="nav-icon-container">
            <NavLink
              to={item.link}
              activeClassName="active"
              onClick={() => {
                setActive(item.id);
                toggleMenu(); // Close the menu when an item is clicked
              }}
            >
              <span className="title-tab">{item.title}</span>
            </NavLink>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Navigation;
