import React from 'react';
import './YourComponent.css'; // Import your stylesheet for styling

const YourComponent = () => {
  return (
    <div className="cards-container">
      <div className="card">
        <p className="play-alone-text">If you want to win the game, play alone.</p>
        <img src="/path/to/solitary-athlete-image.jpg" alt="Solitary Athlete" className="solitary-athlete-image" />
      </div>
      <div className="card">
        <p className="play-together-text">If you want to win championships, play together.</p>
        <img src="/path/to/team-celebration-image.jpg" alt="Team Celebration" className="team-celebration-image" />
      </div>
      {/* Repeat the above structure for the remaining 4 cards */}
      {/* ... */}
    </div>
  );
};

export default YourComponent;
