import React, { useState } from 'react';

const Form = ({ handleFlashMessage }) => {
  const [formData, setFormData] = useState({
    name: '',
    company: '',
    email: '',
    phone: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('this is the form data', formData)
    try {
      const response = await fetch('http://localhost:3001/api/leads', { 
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Lead created successfully:', data);

        setFormData({
          name: '',
          company: '',
          email: '',
          phone: '',
        });

        handleFlashMessage('Contacto enviado, correremos a atenderte!');
      } else {
        console.error('Failed to create lead:', response.statusText);
        handleFlashMessage('Failed to submit form. Please try again.');
      }
    } catch (error) {
      console.error('Error creating lead:', error);
      handleFlashMessage('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="form-view-container">
      <h1>Cont√°ctanos</h1>
      <form onSubmit={handleSubmit} className="form-container">
        {/* Form inputs */}
        <button type="submit" id="submit-button">Enviar</button>
      </form>
    </div>
  );
};

export default Form;
