import React, { useState } from 'react';
import { pets } from '../../utils/icons';
import { NavLink, Link } from 'react-router-dom';
import { menuItems } from '../../utils/menuItems';
// import { signOutIcon } from '../../utils/icons';
import { useGlobalContext } from '../../context/globalContext';

export const Navigation = () => {
  const [active,setActive] = useState('')
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // const signOut = () => {
  //   removeCookie('Email'); add isAuthenticated and removeCookie to context
  //   removeCookie('AuthToken');
  //   console.log('removed');
  //   window.location.reload();
  // };

  return (
    <nav>
      <Link to="/" className="title">
        <div className="logo">
          {pets}
          <h1>Kimi</h1>
        </div>
      </Link>
      <div className="menu" onClick={() => setMenuOpen(!menuOpen)}>
        <span></span>
        <span></span>
        <span></span>
      </div>
      <ul className={`menu-list ${menuOpen ? 'open' : ''}`}>
        {menuItems.map((item) => (
          <li key={item.id} className="nav-icon-container">
            <NavLink
              to={item.link}
              activeClassName="active"
              onClick={() => {
                setActive(item.id);
                toggleMenu(); // Close the menu when an item is clicked
              }}
            >
              <span className="title-tab">{item.title}</span>
            </NavLink>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Navigation;
