import React from 'react';

const ServiceDescription = ({ photo, descriptionPoints, isLeftAligned }) => {
  const photoClass = isLeftAligned ? 'service-photo-right' : 'service-photo-left';
  const descriptionClass = isLeftAligned ? 'description-container-left' : 'description-container';

  return (
    <div className="service-description-container">
      {isLeftAligned && <div className={descriptionClass}>
        {descriptionPoints.map((point, index) => (
          <p key={index}>{point}</p>
        ))}
      </div>}
      <img src={photo} alt="Service" className={photoClass} />
      {!isLeftAligned && <div className={descriptionClass}>
        {descriptionPoints.map((point, index) => (
          <p key={index}>{point}</p>
        ))}
      </div>}
    </div>
  );
};

export default ServiceDescription;
