import React, { useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { menuItems } from '../../utils/menuItems';
import logoImage from '../../images/white_logo.png'; // Import your logo image
import { facebook } from '../../utils/icons';
import { instagram } from '../../utils/icons';
import { linkedin } from '../../utils/icons';

export const Navigation = () => {
  const [active, setActive] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav>
      <Link to="/" className="title">
        <div>
          <img src={logoImage} alt="Logo" id="logo-nav" /> 
        </div>
      </Link>

      <ul className={`menu-list ${menuOpen ? 'open' : ''}`}>
        {menuItems.map((item) => (
          <li key={item.id} className="nav-icon-container">
            <NavLink
              to={item.link}
              activeClassName="active"
              onClick={() => {
                setActive(item.id);
                toggleMenu(); 
              }}
            >
              <span className="title-tab">{item.title}</span>
            </NavLink>
          </li>
        ))}
      </ul>

      <div className='socials-logo'>
        <ul className="social-icons">
         {facebook}
         {instagram}
         {linkedin}
        </ul>

      </div>
    </nav>
  );
};

export default Navigation;
